import React from 'react';
import logo from './logo.svg';
import { BrowserRouter, Route, Link } from 'react-router-dom';
import './App.css';
import HomeScreen from './screens/HomeScreen';
import ProductScreen from './screens/ProductScreen';
import CartScreen from './screens/CartScreen';
import SignInScreen from './screens/SignInScreen';
import { useSelector } from 'react-redux';
import RegisterScreen from './screens/RegisterScreen';
import ProductsScreen from './screens/ProductsScreen';
import ShippingScreen from './screens/ShippingScreen';
import PaymentScreen from './screens/PaymentScreen';
import PlaceOrderScreen from './screens/PlaceOrderScreen';
import ProfileScreen from './screens/ProfileScreen';
import OrderScreen from './screens/OrderScreen';
import OrdersScreen from './screens/OrdersScreen';


function App() {

  const cart = useSelector(state => state.cart);

  const { cartItems } = cart;

  const userSignin = useSelector(state=>state.userSignin);
  const {userInfo} = userSignin;
  // console.log(userInfo.isAdmin);
  // console.log(userInfo);
  var isAdminn;// = false;
  if(userInfo){
    if (userInfo.isAdmin){
      isAdminn = userInfo.isAdmin;
    }

  }
  // else{
  //   isAdminn = false;
  // }
  // else{
  //   isAdminn = false;
  // }
  console.log(isAdminn);
  // const isAdminn = userInfo.isAdminn || false;
  // console.log(isAdminn);

  const openMenu = () =>{
    document.querySelector(".sidebar").classList.add("open");
  }

  const closeMenu = () =>{
    document.querySelector(".sidebar").classList.remove("open");
  }

  return (
    <BrowserRouter>
    <div className="grid-container">
    <header className="header navbar navbar-expand-lg navbar-light bg-light">
      {/* <div className="brand navbar-brand"> */}
        {/* <button className="sidebar-open-button" onClick={openMenu}>
          &#9776;
        </button> */}
        <a className="navbar-brand" href="/">DawaaiWala</a>
        {/* <a href="index.html">DawaaiWala</a> */}
      {/* </div> */}
      <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavDropdown" aria-controls="navbarNavDropdown" aria-expanded="false" aria-label="Toggle navigation">
    <span class="navbar-toggler-icon"></span>
  </button>
      <div className="collapse navbar-collapse" id="navbarNavDropdown">
      <ul className="navbar-nav">
          <li className="nav-item active">
              <a className="nav-link">Home<span class="sr-only">(current)</span></a>
          </li>
          <li className="nav-item ">
              <Link className="nav-link" to="/about">About</Link>
          </li>
          <li className="nav-item">
              <Link className="nav-link" to="/covid">Covid-19</Link>
          </li>
          <li class="nav-item dropdown">
        <a class="nav-link dropdown-toggle" href="#" id="navbarDropdownMenuLink" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
          Product Categories
        </a>
        <div class="dropdown-menu" aria-labelledby="navbarDropdownMenuLink">
          <a class="dropdown-item" href="#">Action</a>
          <a class="dropdown-item" href="#">Another action</a>
          <a class="dropdown-item" href="#">Something else here</a>
        </div>
      </li>
      <li className="nav-item">
              <Link className="nav-link" to="/cart">Cart ({cartItems.length})</Link>
          </li>
          <li className="nav-item">
          {
          userInfo ? (<Link to="/profile">{userInfo.name}</Link>):

          (<Link to="/signin">Log In</Link>)
        }
          </li>
          <li className="nav-item">
          {userInfo && userInfo.isAdmin && (
              <div className="dropdown">
                <a href="#">Admin</a>
                <ul className="dropdown-content">
                  <li>
                    <Link to="/orders">Orders</Link>
                    <Link to="/products">Products</Link>
                  </li>
                </ul>
              </div>)}
          </li>
      </ul>
        {/* <a href="#">About</a>
        <a href="#">Covid-19</a>
        <a href="/cart">Cart ({cartItems.length})</a> */}
        {/* {
          userInfo ? (<Link to="/profile">{userInfo.name}</Link>):

          (<Link to="/signin">Log In</Link>)
        } */}
        {/* <a href="signin.html">Sign In</a> */}
        {/* {userInfo && userInfo.isAdmin && (
              <div className="dropdown">
                <a href="#">Admin</a>
                <ul className="dropdown-content">
                  <li>
                    <Link to="/orders">Orders</Link>
                    <Link to="/products">Products</Link>
                  </li>
                </ul>
              </div> */}
            {/* )} */}
      </div>
    </header>

  <aside className="sidebar">
    <h3>Select Categories</h3>
    <button className="sidebar-close-button" onClick={closeMenu}>Close</button>
    <ul className="categories">
      <li>
        <Link to="/type/Tablet">Tablet</Link>
      </li>
      <li>
        <Link to="/type/Syrup">Syrup</Link>
      </li>
      <li>
        <Link to="/type/Herb">Herb</Link>
      </li>
      <li>
        <Link to="/type/Device">Device</Link>
      </li>


    </ul>
  </aside>

    <main className="main">
      <div className="content">

      <Route path="/profile" component={ProfileScreen} />
      {isAdminn &&
        <Route path="/products" component={ProductsScreen}></Route>
      }
      <Route path="/type/:id" component={HomeScreen} />
      {/* <Route path="/category/:id" component={HomeScreen} /> */}
      {/* <Route path="/order/cart" component={CartScreen}></Route> */}
      <Route path="/shipping" component={ShippingScreen}></Route>
      <Route path="/payment" component={PaymentScreen}></Route>
      <Route path="/placeorder" component={PlaceOrderScreen}></Route>
      <Route path="/signin" component={SignInScreen}></Route>
      <Route path="/register" component={RegisterScreen}></Route>
      <Route path="/product/:id" component={ProductScreen} />
      <Route path="/cart/:id?" component={CartScreen}></Route>
      <Route path="/" exact={true} component={HomeScreen} />
      <Route path="/order/:id" component={OrderScreen} />
      { isAdminn &&
        <Route path="/orders" component={OrdersScreen} />
      }
      </div>

    </main>
    <footer className="footer">
      All Rights Reserved © 2020. DawaaiWala
    </footer>
  </div>
  </BrowserRouter>
  );
}

export default App;
