// import React from 'react';

import React, { useEffect, useState } from 'react';

import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { signin } from '../actions/userActions';



function SigninScreen(props) {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const userSignin = useSelector(state => state.userSignin);
    const {loading, userInfo, error} = userSignin;
    const dispatch = useDispatch();
    const redirect =  props.location.search?props.location.search.split("=")[1]:'/';
    useEffect(() => {
        if(userInfo){
            props.history.push(redirect);
        }

        return () => {
            //
        };
    }, [userInfo]);


const submitHandler = (e) => {
    e.preventDefault();
    dispatch(signin(email, password));
}

    return <div className="form login-form">
        <form onSubmit={submitHandler}>
            <ul className="form-container">

            <li>
                <h2 className="title-heading">Log In</h2>
            </li>
            <li>
                {loading && <div>Loading</div>}
                {error && <div>{error.status}</div>}
            </li>
               <li>
                   <label htmlFor="email">
                       Email
                   </label>
                   <input type="email" name="email" id="email" onChange={(e) => setEmail(e.target.value)}>

                   </input>
               </li> 
               <li>
                   <label htmlFor="password">
                       Password
                   </label>
                   <input type="password" id="password" name="password" onChange={(e)=>setPassword(e.target.value)}>

                   </input>
               </li>
               <li>
                   <button type="submit" className="btn btn-lg btn-primary">Log In</button>
               </li>
               <li>
                   New to DawaaiWalay?
               </li>
               <li>
                   <Link to={redirect === "/" ? "register": "register?redirect=" + redirect} className="btn btn-lg btn-secondary">Register as a new user!</Link>
               </li>
            </ul>
        </form>
    </div>
}

export default SigninScreen;
