// import React from 'react';

import React, { useEffect, useState } from 'react';

import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { register } from '../actions/userActions';



function RegisterScreen(props) {

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [rePassword, setRePassword] = useState('');
    const userRegister = useSelector(state => state.userRegister);
    const {loading, userInfo, error} = userRegister;
    const dispatch = useDispatch();

    const redirect =  props.location.search?props.location.search.split("=")[1]:'/';

    useEffect(() => {
        if(userInfo){
            props.history.push(redirect);
    window.location.reload();

        }

        return () => {
            //
        };
    }, [userInfo]);


const submitHandler = (e) => {
    e.preventDefault();
    dispatch(register(name, email, password));
}

    return <div className="form">
        <form onSubmit={submitHandler}>
            <ul className="form-container">

            <li>
                <h2 className="title-heading">Create Your Account</h2>
            </li>
            <li>
                {loading && <div>Loading</div>}
                {error && <div>{error}</div>}
            </li>
               <li>
                   <label htmlFor="name">
                       Name
                   </label>
                   <input type="name" name="name" id="name" onChange={(e) => setName(e.target.value)}>

                   </input>
               </li> 
               <li>
                   <label htmlFor="email">
                       Email
                   </label>
                   <input type="email" name="email" id="email" onChange={(e) => setEmail(e.target.value)}>

                   </input>
               </li>
               <li>
                   <label htmlFor="password">
                       Password
                   </label>
                   <input type="password" id="password" name="password" onChange={(e)=>setPassword(e.target.value)}>

                   </input>
               </li>
               <li>
                   <label htmlFor="rePassword">
                       Re-Enter Password
                   </label>
                   <input type="password" id="rePassword" name="rePassword" onChange={(e)=>setRePassword(e.target.value)}>

                   </input>
               </li>
               <li>
                   <button type="submit" className="btn btn-lg btn-primary">Register</button>
               </li>
               <li>
                   Already A User? <Link to={redirect === "/" ? "signin": "signin?redirect=" + redirect} className="btn btn-lg btn-secondary">Register as a new user!</Link>

               </li>
               
            </ul>
        </form>
    </div>
}

export default RegisterScreen;
